import * as React from 'react';
import { StyleSheet, Text, View, FlatList, Image, TouchableOpacity } from 'react-native';



export function Profile({ route, navigation }) {

  const { name } = route.params;

  return (
    <View style={styles.container}> 
    <Text>Profile SCREEN {name}</Text>
    <FlatList
     data = {[{title: 'Dam Van Thuc ', key:'19/07/2000', anh:'https://genk.mediacdn.vn/2019/11/30/1-15751247013311481884219.jpg'},
      {title:'Do Cao', key:'22/10/1996', anh:'https://genk.mediacdn.vn/2019/11/30/1-15751247013311481884219.jpg'},
      {title:'Le Van Toong', key:'11/03/2000', anh:'https://genk.mediacdn.vn/2019/11/30/1-15751247013311481884219.jpg'},
      {title:'Nguyễn Hoai Sơn', key:'23/02/2000', anh:'https://genk.mediacdn.vn/2019/11/30/1-15751247013311481884219.jpg'},
      {title:'Le Hong Phong', key:'14/02/2000', anh:'https://genk.mediacdn.vn/2019/11/30/1-15751247013311481884219.jpg'}
     ]}
     renderItem={({item}) =>
<TouchableOpacity onPress = {() => alert(item.title +  ' : ' + item.key)}>
     <View 
      style={{
      flex: 1,
      flexDirection: 'column',
      borderColor: 'black',
      borderWidth: 1,
      margin:5,
  }}>
      <View style={{ 
      flex: 1,
      flexDirection: 'row',

      }}>
          <Image 
              source={item.anh}
              style={{width:100, height:100, margin:5, backgroundColor:'blue'}}
          >
          </Image>
          <View 
          style={{
              flexDirection: 'column'}}>
              <View>
              <Text style={styles.flatListItem}>Họ tên: {item.title}</Text>
              <Text style={styles.flatListItem1}>Năm sinh: {item.key}</Text>
              </View>
          </View>
      </View>

      <View style={{
          flex: 1,
          backgroundColor: 'white',
          height: 4
      }}>
      </View>
  </View>
  </TouchableOpacity>

    }
    />
  </View>
  );
}
  const styles = StyleSheet.create({
    container: {
      marginTop: 50,
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    flatListItem:{
     marginVertical:5,
      marginHorizontal:10,
      fontSize: 30,
      fontWeight:'bold'
    },
    flatListItem1:{
      marginVertical:5,
       marginHorizontal:10,
    }
  });
