import * as React from 'react';
import { StyleSheet, Text, View, FlatList, Image, TouchableOpacity, Button } from 'react-native';
import  { useState} from 'react';


export function Profile({ route, navigation }) {
  const {title} = route.params;
  const { name } = route.params;
  const [dulieu, getDulieu] = useState([]);


function read(){
  fetch('http://dotplays.com/wp-json/wp/v2/posts?_embed')
        .then(response => response.json())
        .then(json => {getDulieu(json)})
}
searchFilterFunction = text => {    
  const newData = this.arrayholder.filter(item => {      
    const itemData = `${item.name.title.toUpperCase()}   
    ${item.name.first.toUpperCase()} ${item.name.last.toUpperCase()}`;
     const textData = text.toUpperCase();

     return itemData.indexOf(textData) > -1;    
  });    
  this.setState({ data: newData });  
};

makeRemoteRequest = () => {    
  const url = `http://dotplays.com/wp-json/wp/v2/search?search=android`;
  this.setState({ loading: true });
  fetch(url)      
    .then(res => res.json())      
    .then(res => {        
      this.setState({          
        data: res.results,          
        error: res.error || null,          
        loading: false,        
      });        
     this.arrayholder = res.results;      
   })      
   .catch(error => {        
     this.setState({ error, loading: false });      
   });  
};

renderHeader = () => {    
  return (      
    <SearchBar        
      placeholder="Seach"        
      lightTheme        
      round        
      onChangeText={text => this.searchFilterFunction(text)}
      autoCorrect={false}             
    />    
  );  
};
  return (
    <View style={styles.container}> 
     {read()}
     {renderHeader()}
    <FlatList
     data = {dulieu}
     renderItem={({item}) =>
    <TouchableOpacity onPress = {() => navigation.navigate('Detail',{name : item.link},{title : item.title.rendered})}>
     <View 
      style={{
      flex: 1,
      flexDirection: 'column',
      borderColor: 'black',
      borderWidth: 1,
      margin:5,
  }}>
      <View style={{ 
      flex: 1,
      flexDirection: 'row',

      }}>
          <Image 
              source={{ uri : item._embedded['wp:featuredmedia'][0].source_url }}
              style={{width:100, height:100, margin:5, backgroundColor:'blue'}}
          >
          </Image>
          <View 
          style={{
              flexDirection: 'column'}}>
              <View>
              <Text style={styles.flatListItem}> {item.title.rendered}</Text>
              <Text style={styles.flatListItem1}>{item.date_gmt}</Text>
              </View>
          </View>
      </View>

      <View style={{
          flex: 1,
          backgroundColor: 'white',
          height: 4
      }}>
      </View>
  </View>
  </TouchableOpacity>

    }
    />
  </View>
  );
}
  const styles = StyleSheet.create({
    container: {
      marginTop: 50,
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    flatListItem:{
     marginVertical:5,
      marginHorizontal:10,
      fontSize: 30,
      fontWeight:'bold'
    },
    flatListItem1:{
      marginVertical:5,
       marginHorizontal:10,
    }
  });
