import * as React from 'react';
import { StyleSheet, Text, View, FlatList, Image, TouchableOpacity,Button } from 'react-native';
import  { useState} from 'react';


export function Profile({ route, navigation }) {

  const { name } = route.params;
  const [dulieu, getDulieu] = useState([]);
  return (
    <View style = {styles.container}>
         <Text>click on GET</Text>
         {
        fetch('http://dotplays.com/wp-json/wp/v2/posts?_embad')
        .then(response => response.json())
        .then(json => {getDulieu(json)})
         }
        <FlatList
        data = {dulieu}
        renderItem = {({item})=> 
      <TouchableOpacity>
      <View style = {styles.item}>
         {/* <Image style = {{height : 100, width : 100}} 
         source = {{uri : item._links["wp:featuredmedia"][0].source_url}}/> */}
        <Text>{item.title.rendered}</Text>
        <Text>{item.date_gmt}</Text>
      </View>
      </TouchableOpacity>
      }
        />
       </View>
  );
}
  const styles = StyleSheet.create({
    container: {
      marginTop: 50,
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    flatListItem:{
     marginVertical:5,
      marginHorizontal:10,
      fontSize: 30,
      fontWeight:'bold'
    },
    flatListItem1:{
      marginVertical:5,
       marginHorizontal:10,
    }
  });
