import * as React from 'react';
import { StyleSheet, Text, View, FlatList, Image, TouchableOpacity, Button } from 'react-native';
import  { useState} from 'react';


export function Profile({ route, navigation }) {

  const { name } = route.params;
  const [dulieu, getDulieu] = useState([]);
function read(){
  fetch('http://dotplays.com/wp-json/wp/v2/posts?_embad')
        .then(response => response.json())
        .then(json => {getDulieu(json)})
}
  return (
    <View style={styles.container}> 
    <Text>Profile SCREEN {name}</Text>
     {read()}
    <FlatList
     data = {dulieu}
     renderItem={({item}) =>
<TouchableOpacity onPress = {() => navigation.navigate('Detail', {name: 'ĐÀM VĂN THỨC'})}>
     <View 
      style={{
      flex: 1,
      flexDirection: 'column',
      borderColor: 'black',
      borderWidth: 1,
      margin:5,
  }}>
      <View style={{ 
      flex: 1,
      flexDirection: 'row',

      }}>
          <Image 
              source={{uri : item._embedded['wp:featuredmedia'][0].source_url }}
              style={{width:100, height:100, margin:5, backgroundColor:'blue'}}
          >
          </Image>
          <View 
          style={{
              flexDirection: 'column'}}>
              <View>
              <Text style={styles.flatListItem}> {item.title.rendered}</Text>
              <Text style={styles.flatListItem1}>{item.date_gmt}</Text>
              </View>
          </View>
      </View>

      <View style={{
          flex: 1,
          backgroundColor: 'white',
          height: 4
      }}>
      </View>
  </View>
  </TouchableOpacity>

    }
    />
  </View>
  );
}
  const styles = StyleSheet.create({
    container: {
      marginTop: 50,
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'center',
      justifyContent: 'center',
    },
    flatListItem:{
     marginVertical:5,
      marginHorizontal:10,
      fontSize: 30,
      fontWeight:'bold'
    },
    flatListItem1:{
      marginVertical:5,
       marginHorizontal:10,
    }
  });
