import * as React from 'react';
import {  Text, View, Button } from 'react-native';



export function Home({navigation}) {

  return (
    <ImageBackground source={require('./assets/backgroud.jpg')} style={styles.container}>

       <View style={styles.containerLogo}>
         <Image source={img} style={styles.logo}/>
        </View>

         <View style={styles.infor}>
        <View>
        <AntDesign style={styles.icon} name="user" size={20} color="black" />

         <TextInput 
         placeholder='UserName'
         style={styles.TextInputs}></TextInput>
         </View>

         <View>
         <AntDesign style={styles.icon} name="lock" size={20} color="black" />
         <TextInput
         placeholder='Password'
         style={styles.TextInputs}></TextInput>
        </View>
        <TouchableOpacity
         style={styles.login}
         onPress = {() => navigation.navigate('Profile', {name: 'Cao'})}>
          <Text style={styles.Texts}>SIGN IN</Text>
        </TouchableOpacity>

        <Text style={{marginTop:40,color:'white'}}
        onPress={() => alert('Forget Passwors')}
        >Forget Password ?</Text>
        <Text style={{marginTop:25,color:'white'}}>___________________________________________</Text>
        <View 
        style={{flex:1, flexDirection:'row',alignContent:'center'}}>
        <Text 
        style={{marginTop:25,
        color:'white',}} 

        >Don't have a account ?</Text> 
        <Text 
        style={{marginTop:25,
        color:'#0099ff',
        fontWeight:'bold'
      }}
        onPress={() => alert('SIGN UP')}
            > SIGN UP</Text>
        </View>
      </View>
      </ImageBackground>

  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection:'column',
    width: null,
    height:null,
    alignItems: 'center',
    justifyContent: 'center',
  },
  containerLogo:{
    flex:1,
    flexDirection:'row',
    alignContent:'center',
    alignItems:'center',
  },
  infor:{
    flex:1,
    flexDirection:'column',
    alignItems:'center',
    alignContent:'center'
  },
  logo:{
    height:150,
    width: 200,
  },
  TextInputs:{
    width: 300,
    height: 40,
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
    color: 'rgba(0,10, 255,1)',
    borderRadius:20,
    margin:5,
    paddingLeft: 35,

  },
  login:
  {
    height:40,
    borderRadius:10,
    margin:5,
    marginTop:20,
    width:300,
    borderRadius:20,
    backgroundColor:'#00CCFF',
    alignItems:'center'
  },
  Texts:{
    fontWeight:'bold',
    color:'white',
    marginTop:9
  },
 icon:{
    color:'black',
    position:'absolute',
    left:15,
    top: 12,
 }
});
